mapper.select:
select count(*) from payv2_buss_company  


		and id = #{id}


		and channel_id = #{channelId}


		and user_name like CONCAT('%',#{userName},'%')


		and pass_word like CONCAT('%',#{passWord},'%')


		and pay_pass_word like CONCAT('%',#{payPassWord},'%')


		and company_key like CONCAT('%',#{companyKey},'%')


		and company_secret like CONCAT('%',#{companySecret},'%')


		and company_name like CONCAT('%',#{companyName},'%')


		and company_photo like CONCAT('%',#{companyPhoto},'%')


		and company_scale = #{companyScale}


		and company_type = #{companyType}


		and company_trade = #{companyTrade}


		and company_trade_var like CONCAT('%',#{companyTradeVar},'%')


		and company_range_province = #{companyRangeProvince}


		and company_range_city = #{companyRangeCity}


		and company_addr like CONCAT('%',#{companyAddr},'%')


		and license_num like CONCAT('%',#{licenseNum},'%')


		and license_addr like CONCAT('%',#{licenseAddr},'%')


		and license_pic like CONCAT('%',#{licensePic},'%')


		and organization_code like CONCAT('%',#{organizationCode},'%')


		and organization_code_url like CONCAT('%',#{organizationCodeUrl},'%')


		and other_information like CONCAT('%',#{otherInformation},'%')


		and legal_name like CONCAT('%',#{legalName},'%')


		and legal_id_card like CONCAT('%',#{legalIdCard},'%')


		and account_type = #{accountType}


		and account_bank like CONCAT('%',#{accountBank},'%')


		and account_name like CONCAT('%',#{accountName},'%')


		and account_card like CONCAT('%',#{accountCard},'%')


		and way_arrival_type = #{wayArrivalType}


		and way_arrival_value = #{wayArrivalValue}


		and wechat_account_card like CONCAT('%',#{wechatAccountCard},'%')


		and alipay_account_card like CONCAT('%',#{alipayAccountCard},'%')


		and license_type = #{licenseType}


		and contacts_name like CONCAT('%',#{contactsName},'%')


		and contacts_phone like CONCAT('%',#{contactsPhone},'%')


		and contacts_mail like CONCAT('%',#{contactsMail},'%')


		and company_status = #{companyStatus}


		and company_pass_reason like CONCAT('%',#{companyPassReason},'%')


		and is_random = #{isRandom}


		and is_delete = #{isDelete}


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d')

