mapper.select:
select id,app_id,company_id,channel_id,rate_id,way_id,no_pay_amount,no_pay_count,pay_amount,pay_count,refund_amount,refund_count,pay_net,rate_amount,rate_cost,rate_profit,record_time,create_time from pay_data_record


		and id=#{id}


		and app_id=#{appId}


		and company_id=#{companyId}


		and channel_id=#{channelId}


		and rate_id=#{rateId}


		and way_id=#{wayId}


		and no_pay_amount=#{noPayAmount}


		and no_pay_count=#{noPayCount}


		and pay_amount=#{payAmount}


		and pay_count=#{payCount}


		and refund_amount=#{refundAmount}


		and refund_count=#{refundCount}


		and pay_net=#{payNet}


		and rate_amount=#{rateAmount}


		and rate_cost=#{rateCost}


		and rate_profit=#{rateProfit}


		and record_time=#{recordTime}


		and create_time=#{createTime}

select id,app_id,company_id,channel_id,rate_id,way_id,no_pay_amount,no_pay_count,pay_amount,pay_count,refund_amount,refund_count,pay_net,rate_amount,rate_cost,rate_profit,record_time,create_time from pay_data_record


		and id=#{id}


		and app_id=#{appId}


		and company_id=#{companyId}


		and channel_id=#{channelId}


		and rate_id=#{rateId}


		and way_id=#{wayId}


		and no_pay_amount=#{noPayAmount}


		and no_pay_count=#{noPayCount}


		and pay_amount=#{payAmount}


		and pay_count=#{payCount}


		and refund_amount=#{refundAmount}


		and refund_count=#{refundCount}


		and pay_net=#{payNet}


		and rate_amount=#{rateAmount}


		and rate_cost=#{rateCost}


		and rate_profit=#{rateProfit}


		and record_time=#{recordTime}


		and create_time=#{createTime}


SELECT id,app_id,company_id,channel_id,rate_id,way_id,no_pay_amount,no_pay_count,pay_amount,pay_count,refund_amount,refund_count,pay_net,rate_amount,rate_cost,rate_profit,record_time,create_time FROM pay_data_record 


		and id = #{id}


		and app_id = #{appId}


		and company_id = #{companyId}


		and channel_id = #{channelId}


		and rate_id = #{rateId}


		and way_id = #{wayId}


		and no_pay_amount like  CONCAT('%',#{noPayAmount},'%')


		and no_pay_count = #{noPayCount}


		and pay_amount like  CONCAT('%',#{payAmount},'%')


		and pay_count = #{payCount}


		and refund_amount like  CONCAT('%',#{refundAmount},'%')


		and refund_count = #{refundCount}


		and pay_net like  CONCAT('%',#{payNet},'%')


		and rate_amount like  CONCAT('%',#{rateAmount},'%')


		and rate_cost like  CONCAT('%',#{rateCost},'%')


		and rate_profit like  CONCAT('%',#{rateProfit},'%')


		and record_time  >=  str_to_date(#{recordTimeBegin},  '%Y-%m-%d') 


		and record_time  <=  str_to_date(#{recordTimeEnd},  '%Y-%m-%d') 


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 



		ORDER BY ${sortName} ${sortOrder}

select sum(pay_amount) as pay_amount, sum(pay_count) as pay_count, IFNULL(sum(no_pay_amount), 0) AS noPayAmount, IFNULL(count(no_pay_count), 0) AS noPayCount, sum(refund_amount) as refund_amount, sum(refund_count) as refund_count, sum(pay_net) as pay_net, sum(rate_amount) as rate_amount from pay_data_record
and app_id=#{appId}
and company_id=#{companyId}
and channel_id=#{channelId}
and rate_id=#{rateId}
and str_to_date(record_time, '%Y-%m-%d %H') <= str_to_date(#{endTime}, '%Y-%m-%d %H')
and str_to_date(record_time, '%Y-%m-%d %H') >= str_to_date(#{startTime}, '%Y-%m-%d %H')

select sum(pay_amount) as pay_amount,sum(pay_count) as pay_count,record_time from pay_data_record


		and app_id=#{appId}


		and company_id=#{companyId}


		and channel_id=#{channelId}


		and rate_id=#{rateId}


		and str_to_date(record_time,  '%Y-%m-%d %H') <= str_to_date(#{endTime},  '%Y-%m-%d %H')


		and str_to_date(record_time,  '%Y-%m-%d %H') >= str_to_date(#{startTime},  '%Y-%m-%d %H')


group by str_to_date(record_time,  '%Y-%m-%d %H')

select sum(pay_amount) as pay_amount ,sum(pay_count) as pay_count,record_time from pay_data_record


		and app_id=#{appId}


		and company_id=#{companyId}


		and channel_id=#{channelId}


		and rate_id=#{rateId}


		and str_to_date(record_time,  '%Y-%m-%d %H') <= str_to_date(#{endTime},  '%Y-%m-%d %H')


		and str_to_date(record_time,  '%Y-%m-%d %H') >= str_to_date(#{startTime},  '%Y-%m-%d %H')


group by str_to_date(record_time,  '%Y-%m-%d')
