select pbca.id,pbca.company_id,pbc.channel_id,pbca.app_secret,pbc.is_random,pbca.android_app_package,pbca.ios_iphone_id
,pbca.app_name,pbc.company_name
 from payv2_buss_company_app as pbca 
join payv2_buss_company as pbc on pbca.company_id = pbc.id

	pbca.app_key=#{appKey} 
	and pbca.app_status=#{appStatus} and pbca.is_delete=#{isDelete}
	and pbc.company_status=#{companyStatus} and pbc.is_delete=#{isDelete}
