mapper.delete:
delete from payv2_buss_company_shop


		and id=#{id}


		and shop_key=#{shopKey}


		and company_id=#{companyId}


		and shop_name=#{shopName}


		and shop_card=#{shopCard}


		and shop_range_province=#{shopRangeProvince}


		and shop_range_city=#{shopRangeCity}


		and lat=#{lat}


		and lon=#{lon}


		and shop_address=#{shopAddress}


		and shop_icon=#{shopIcon}


		and shop_background=#{shopBackground}


		and shop_desc=#{shopDesc}


		and shop_week_time=#{shopWeekTime}


		and shop_day_start_time=#{shopDayStartTime}


		and shop_day_end_time=#{shopDayEndTime}


		and shop_remarks=#{shopRemarks}


		and shop_per_ave_money=#{shopPerAveMoney}


		and shop_contacts=#{shopContacts}


		and shop_contacts_phone=#{shopContactsPhone}


		and shop_email=#{shopEmail}


		and pay_way_id=#{payWayId}


		and code_way_id=#{codeWayId}


		and shop_status=#{shopStatus}


		and shop_pass_reason=#{shopPassReason}


		and shop_two_code_url=#{shopTwoCodeUrl}


		and is_delete=#{isDelete}


		and create_time=#{createTime}


		and update_time=#{updateTime}

