SELECT id,shop_key,company_id,shop_name,shop_card,shop_range_province,shop_range_city,lat,lon,shop_address,shop_icon,shop_background,shop_desc,shop_week_time,shop_day_start_time,shop_day_end_time,shop_remarks,shop_per_ave_money,shop_contacts,shop_contacts_phone,shop_email,pay_way_id,code_way_id,shop_status,shop_pass_reason,shop_two_code_url,is_delete,create_time,update_time FROM payv2_buss_company_shop 


		and id = #{id}


		and shop_key like  CONCAT('%',#{shopKey},'%')


		and company_id = #{companyId}


		and shop_name like  CONCAT('%',#{shopName},'%')


		and shop_card like  CONCAT('%',#{shopCard},'%')


		and shop_range_province = #{shopRangeProvince}


		and shop_range_city = #{shopRangeCity}


		and lat = #{lat}


		and lon = #{lon}


		and shop_address like  CONCAT('%',#{shopAddress},'%')


		and shop_icon like  CONCAT('%',#{shopIcon},'%')


		and shop_background like  CONCAT('%',#{shopBackground},'%')


		and shop_desc like  CONCAT('%',#{shopDesc},'%')


		and shop_week_time like  CONCAT('%',#{shopWeekTime},'%')


		and shop_day_start_time like  CONCAT('%',#{shopDayStartTime},'%')


		and shop_day_end_time like  CONCAT('%',#{shopDayEndTime},'%')


		and shop_remarks like  CONCAT('%',#{shopRemarks},'%')


		and shop_per_ave_money like  CONCAT('%',#{shopPerAveMoney},'%')


		and shop_contacts like  CONCAT('%',#{shopContacts},'%')


		and shop_contacts_phone like  CONCAT('%',#{shopContactsPhone},'%')


		and shop_email like  CONCAT('%',#{shopEmail},'%')


		and pay_way_id = #{payWayId}


		and code_way_id like  CONCAT('%',#{codeWayId},'%')


		and shop_status = #{shopStatus}


		and shop_pass_reason like  CONCAT('%',#{shopPassReason},'%')


		and shop_two_code_url like  CONCAT('%',#{shopTwoCodeUrl},'%')


		and is_delete = #{isDelete}


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d') 



		ORDER BY ${sortName} ${sortOrder}

select count(*)
from payv2_buss_company_shop shop
left join payv2_buss_company comp on shop.company_id=comp.id

	shop.company_id is not null

		and comp.channel_id = #{channelId}


		and shop.id = #{id}


		and shop.company_id = #{companyId}


		and shop.shop_name like '%'||#{shopName}||'%'


		and shop.shop_range_province = #{shopRangeProvince}


		and shop.shop_range_city = #{shopRangeCity}


		and shop.lat = #{lat}


		and shop.lon = #{lon}


		and shop.is_delete = #{isDelete}


		and shop.shop_address like '%'||#{shopAddress}||'%'


		and shop.shop_contacts like '%'||#{shopContacts}||'%'


		and shop.shop_contacts_phone like '%'||#{shopContactsPhone}||'%'


		and shop.shop_status = #{shopStatus}


		and shop.shop_pass_reason like '%'||#{shopPassReason}||'%'


		and shop.shop_two_code_url like '%'||#{shopTwoCodeUrl}||'%'


		and shop.create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and shop.create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and shop.update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and shop.update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d')


SELECT shop.*
from payv2_buss_company_shop shop
left join payv2_buss_company comp on shop.company_id=comp.id

	shop.company_id is not null

		and comp.channel_id = #{channelId}


		and shop.id = #{id}


		and shop.company_id = #{companyId}


		and shop.shop_name like '%'||#{shopName}||'%'


		and shop.shop_range_province = #{shopRangeProvince}


		and shop.shop_range_city = #{shopRangeCity}


		and shop.lat = #{lat}


		and shop.lon = #{lon}


		and shop.is_delete = #{isDelete}


		and shop.shop_address like '%'||#{shopAddress}||'%'


		and shop.shop_contacts like '%'||#{shopContacts}||'%'


		and shop.shop_contacts_phone like '%'||#{shopContactsPhone}||'%'


		and shop.shop_status = #{shopStatus}


		and shop.shop_pass_reason like '%'||#{shopPassReason}||'%'


		and shop.shop_two_code_url like '%'||#{shopTwoCodeUrl}||'%'


		and shop.create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and shop.create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and shop.update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and shop.update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d') 



		ORDER BY ${sortName} ${sortOrder}


		ORDER BY create_time desc

