mapper.select:
select count(*) from payv2_day_company_clear  


		and id = #{id}


		and company_check_id like CONCAT('%',#{companyCheckId},'%')


		and channel_id = #{channelId}


		and company_id = #{companyId}


		and success_count = #{successCount}


		and success_money like CONCAT('%',#{successMoney},'%')


		and refund_count = #{refundCount}


		and refund_money like CONCAT('%',#{refundMoney},'%')


		and rate_money like CONCAT('%',#{rateMoney},'%')


		and rate_profit like CONCAT('%',#{rateProfit},'%')


		and status = #{status}


		and settle_time  >=  str_to_date(#{settleTimeBegin},  '%Y-%m-%d') 


		and settle_time  <=  str_to_date(#{settleTimeEnd},  '%Y-%m-%d') 


		and clear_time  >=  str_to_date(#{clearTimeBegin},  '%Y-%m-%d') 


		and clear_time  <=  str_to_date(#{clearTimeEnd},  '%Y-%m-%d') 


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d')

